/**
* Convert an Array-like object to a real Array.
*/
   Service.array.toArray = (list, start) => {
     start = start || 0;
     var i = list.length - start;
     var ret = new Array(i);
     while (i--) {
       ret[i] = list[i + start];
     }
     return ret ;
   }

