/**
 * hilmacs hapi's standard gate request path
*/
Path.gate = (x,y,z) => {
	if (y == 'secure' ) {
	return  Hapi.gateway.secure + x +  z ;
	} else if (y == 'web') {
		return  Hapi.gateway.web + x +  z  ;
	}else if (y == 'cdn') {
	return  Hapi.gateway.cdn  ;
	}else{
		return  Hapi.gateway.local + x +  z  ;
	}
}
