Service.shuffleArray =  (array) => {
  var d = array.length, a, b;

  // While there remain elements to shuffle...
  while (0 !== d) {

    // Pick a remaining element...
    b = Math.floor(Math.random() * d);
    d -= 1;

    // And swap it with the current element.
    a = array[d];
    array[d] = array[b];
    array[b] = a;
  }

  return array;
}

